    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Question{% endblock %}

{% set answers = [\"A\", \"B\", \"C\"] %}
{% block gobackbtn %}
    <a href=\"{{ path(\"question_index\") }}\" class=\"d-none d-sm-inline-block btn btn-sm btn-primary shadow-sm\">
        <i class=\"fas fa-arrow-left fa-sm text-white-50\"></i> Go back
    </a>
{% endblock %}

{% block page_title %}
    Question
{% endblock %}
{% block main %}


    <table class=\"table\">
        <tbody>
            <tr>
                <th>Id</th>
                <td>{{ question.id }}</td>
            </tr>
            <tr>
                <th>Statement</th>
                <td>{{ question.statement }}</td>
            </tr>
            <tr>
                <th>Answer A</th>
                <td>{{ question.answerA }}</td>
            </tr>
            <tr>
                <th>Answer B</th>
                <td>{{ question.answerB }}</td>
            </tr>
            <tr>
                <th>Answer C</th>
                <td>{{ question.answerC }}</td>
            </tr>
            <tr>
                <th>Right Answer</th>
                <td>{{ answers[question.rightAnswer] }}</td>
            </tr>
            <tr>
                <th>Points</th>
                <td>{{ question.points }}</td>
            </tr>
            <tr>
                <th>Test</th>
                <td><a href=\"{{ path(\"test_show\", {'id': question.test.id}) }}\">{{ question.test.title }}</a></td>
            </tr>
        </tbody>
    </table>
    <div style=\"width: 100%; display:flex; justify-content: flex-end\">
        <a href=\"{{ path('question_edit', {'id': question.id}) }}\"><button type=\"button\" class=\"btn-sm shadow-sm btn btn-primary mt-2 mb-2 mr-2\"><i class=\"far fa-edit\"></i> Edit</button></a>
        {{ include('question/_delete_form.html.twig') }}
    </div>



{% endblock %}
", "question/show.html.twig", "F:\\recruti_webapp\\templates\\question\\show.html.twig");
